import{_ as t,o as s,c as e,U as l}from"./chunks/framework.70292381.js";const f=JSON.parse('{"title":"Flutter 开始 安装","description":"","frontmatter":{"title":"Flutter 开始 安装","date":"2018-03-12T12:00:00.000Z","categories":["Flutter"],"tags":["Flutter"]},"headers":[],"relativePath":"posts/2018-03-12-Flutter_开始_安装.md","filePath":"posts/2018-03-12-Flutter_开始_安装.md"}'),a={name:"posts/2018-03-12-Flutter_开始_安装.md"},o=l(`<h1 id="开始-安装" tabindex="-1">开始 : 安装 <a class="header-anchor" href="#开始-安装" aria-label="Permalink to &quot;开始 : 安装&quot;">​</a></h1><blockquote><p>2018.3.12 初始化 原文链接](<a href="https://flutter.io/get-started/install" target="_blank" rel="noreferrer">https://flutter.io/get-started/install</a>) <a href="https://flutter.io/get-started/install/" target="_blank" rel="noreferrer">https://flutter.io/get-started/install/</a><a href="https://github.com/ggggxiaolong/blog/blob/master/flutter/start/%E5%AE%89%E8%A3%85.md" target="_blank" rel="noreferrer">markdown地址</a></p></blockquote><p>选择你系统平台进行安装：</p><ol><li><a href="https://flutter.io/setup-windows" target="_blank" rel="noreferrer">Windows</a></li><li><a href="https://flutter.io/setup-macos" target="_blank" rel="noreferrer">Mac</a></li><li><a href="https://flutter.io/setup-linux" target="_blank" rel="noreferrer">Linux</a></li></ol><p><strong>注意</strong> 如果你是中国用户，请先阅读下面的内容 :</p><p>如果你在中国地区使用 Flutter , 使用一个可靠的本地 Flutter 镜像可以帮助你 . 通过设置两个环境变量来改变 Flutter 工具使用备用的存储位置(镜像) . 在 运行 <code>flutter</code> 命令前需要配置好 <code>PUB_HOSTED_URL</code> <code>FLUTTER_STORAGE_BASE_URL</code>环境变量 .</p><p>以 MacOS 或Linux 为例 , 通过以下几步配置环境变量 . 在你要存储 Flutter 的地址 运行下面的 Shell 命令</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#C792EA;">export</span><span style="color:#A6ACCD;"> PUB_HOSTED_URL</span><span style="color:#89DDFF;">=</span><span style="color:#C3E88D;">https://pub.flutter-io.cn</span></span>
<span class="line"><span style="color:#C792EA;">export</span><span style="color:#A6ACCD;"> FLUTTER_STORAGE_BASE_URL</span><span style="color:#89DDFF;">=</span><span style="color:#C3E88D;">https://storage.flutter-io.cn</span></span>
<span class="line"><span style="color:#FFCB6B;">git</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">clone</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-b</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">dev</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://github.com/flutter/flutter.git</span></span>
<span class="line"><span style="color:#C792EA;">export</span><span style="color:#A6ACCD;"> PATH</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">$PWD</span><span style="color:#C3E88D;">/flutter/bin:</span><span style="color:#A6ACCD;">$PATH</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#82AAFF;">cd</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">./flutter</span></span>
<span class="line"><span style="color:#FFCB6B;">flutter</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">doctor</span></span></code></pre></div><p>通过以上几步，你就可以开始 Flutter 之后的工作了。 这里通过<code>flutter packages get</code>获取的包会通过 <code>flutter-io.cn</code> 地址下载 (shell 环境可以获取到上面设置的环境变量前提下).</p>`,9),r=[o];function n(p,c,i,_,u,d){return s(),e("div",null,r)}const A=t(a,[["render",n]]);export{f as __pageData,A as default};
